import React, { useState } from 'react';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import { format, parse, startOfWeek, getDay, parseISO } from 'date-fns';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { FaHistory } from 'react-icons/fa';
import HistorySidebar from '../components/HistorySidebar';
import CreateTodoModal from '../components/CreateTodoModal';

const locales = {
  'en-US': require('date-fns/locale/en-US'),
};

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

// Mock events
const events = [
  {
    id: 0,
    title: 'Sprint Planning',
    start: parseISO('2025-06-16T09:15:00'),
    end: parseISO('2025-06-16T10:00:00'),
    platform: 'Zoom',
    zapyInvited: true,
  },
  {
    id: 1,
    title: 'Refinement Messaging',
    start: parseISO('2025-06-19T13:00:00'),
    end: parseISO('2025-06-19T13:50:00'),
    platform: 'Teams',
    zapyInvited: true,
  },
    {
    id: 3,
    title: 'Retrospective Meeting',
    start: parseISO('2025-06-17T13:00:00'),
    end: parseISO('2025-06-17T13:50:00'),
    platform: 'Google Meet',
    zapyInvited: false,
  },
    {
    id: 3,
    title: 'standup',
    start: parseISO('2025-06-18T10:00:00'),
    end: parseISO('2025-06-18T10:15:00'),
    platform: 'Google Meet',
    zapyInvited: true,
  },
    {
    id: 3,
    title: 'design review',
    start: parseISO('2025-06-18T11:00:00'),
    end: parseISO('2025-06-18T11:45:00'),
    platform: 'Google Meet',
    zapyInvited: true,
  },
    {
    id: 3,
    title: 'workshop',
    start: parseISO('2025-06-20T10:00:00'),
    end: parseISO('2025-06-20T14:00:00'),
    platform: 'Conference Room & Zoom',
    zapyInvited: true,
  },
];


const mockTranscript = `Backlog refinement meeting agenda

Vision Alignment (2 mins)
Quickly revisit the broader company mission. What is our purpose and overarching goal?

Last Session Review (5 mins)
Reflect on the previous iteration or sprint:
• What were the achievements?
• What are the pending tasks and why?

New Items to Address (5 mins)
• Time to address emerging concerns:
• Any urgent bugs or defects identified?
• Any fresh feedback from users or stakeholders?
• Are there new features or requirements to consider?

Meeting Notes:
- Registration flow needs to be split into frontend and backend tasks.
- Payment integration requires additional validation steps.
- Timeline for both tasks is set for next sprint.
`;

export default function VoiceAssistant() {
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [meetingStatus, setMeetingStatus] = useState('idle'); // idle, joining, joined, ended, transcription
  const [historyOpen, setHistoryOpen] = useState(false);
  const [meetingHistory, setMeetingHistory] = useState([]);
  const [todoList, setTodoList] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [showTodoModal, setShowTodoModal] = useState(false);
  const [showTodoList, setShowTodoList] = useState(false);
  const [todoItems, setTodoItems] = useState([
    "Registration flow needs to be split into frontend and backend tasks.",
    "Payment integration requires additional validation steps.",
    "Timeline for both tasks is set for next sprint."
  ]);
  const [editMode, setEditMode] = useState(false);
  const [editedItems, setEditedItems] = useState([...todoItems]);

  // Simulate extracting action items from transcript
  const actionItems = [
    "Split registration flow into frontend and backend tasks",
    "Add validation steps to payment integration",
    "Set timeline for both tasks for next sprint"
  ];

  const handleJoin = () => {
    setMeetingStatus('joining');
    setTimeout(() => {
      setMeetingStatus('joined');
      setTimeout(() => {
        setMeetingStatus('ended');
        setTimeout(() => {
          setMeetingStatus('transcription');
          // Save to history
          setMeetingHistory(prev => [
            {
              title: selectedEvent.title,
              date: selectedEvent.start.toLocaleString(),
              transcript: mockTranscript
            },
            ...prev
          ]);
        }, 5000);
      }, 5000);
    }, 5000);
  };

  const handleCreateTodo = (todos) => {
    setTodoList(todos);
    setModalOpen(false);
  };

  const handleCreateTodoClick = () => {
    setShowTodoModal(true);
  };

  return (
    <div className="min-h-screen bg-black flex flex-row items-start py-10 px-2 relative">
      {/* Calendar */}
      <div className="max-w-2xl w-full">
        <div className="bg-white text-gray-900 rounded-xl shadow p-6 mb-8">
          <h2 className="text-xl font-semibold mb-4 text-blue-700">Google Calendar View (Mock)</h2>
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            style={{ height: 500 }}
            onSelectEvent={event => {
              setSelectedEvent(event);
              setMeetingStatus('idle');
              setTodoList([]);
            }}
            eventPropGetter={event => ({
              style: {
                backgroundColor: event.zapyInvited ? '#bbf7d0' : '#fef08a',
                color: '#222',
                borderRadius: '6px',
                border: event.zapyInvited ? '2px solid #22c55e' : '2px solid #eab308',
              }
            })}
          />
        </div>
      </div>
      {/* Right Sidebar Panel */}
      <div className="w-[420px] ml-2 relative">
        {/* History Icon */}

        <div className="bg-white rounded-xl shadow p-6 mb-8 text-gray-800 min-h-[400px] flex flex-col justify-start">
          {!selectedEvent && (
            <div className="text-gray-500 text-center">Select a meeting from the calendar</div>
          )}
          {selectedEvent && (
            <>
              <h3 className="text-lg font-semibold mb-2">{selectedEvent.title}</h3>
              <div className="mb-2">
                <span>Platform: {selectedEvent.platform || 'N/A'}</span>
              </div>
              <div className="mb-2">
                <span>Start: {selectedEvent.start.toLocaleString()}</span>
              </div>
              <div className="mb-2">
                <span>End: {selectedEvent.end.toLocaleString()}</span>
              </div>
              <div className="mb-4">
                {selectedEvent.zapyInvited ? (
                  <span className="inline-block bg-green-100 text-green-700 px-2 py-1 rounded">Zapy AI Voice Assistant Invited</span>
                ) : (
                  <span className="inline-block bg-yellow-100 text-yellow-700 px-2 py-1 rounded">Zapy AI Voice Assistant Not Added</span>
                )}
              </div>
              {/* Meeting Status Flow */}
              {meetingStatus === 'idle' && (
                <button
                  className="bg-blue-600 text-white px-4 py-2 rounded shadow hover:bg-blue-700 transition w-full"
                  onClick={handleJoin}
                >
                  Join with Zapy AI Voice Assistant
                </button>
              )}
              {meetingStatus === 'joining' && (
                <div className="flex flex-col items-center mt-6">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mb-2"></div>
                  <span className="text-blue-700 font-semibold">Joining...</span>
                </div>
              )}
              {meetingStatus === 'joined' && (
                <div className="flex flex-col items-center mt-6">
                  <div className="animate-pulse rounded-full h-8 w-8 bg-green-400 mb-2 flex items-center justify-center">
                    <span className="text-white font-bold text-lg">✓</span>
                  </div>
                  <span className="text-green-700 font-semibold">Joined (Taking notes...)</span>
                </div>
              )}
              {meetingStatus === 'ended' && (
                <div className="flex flex-col items-center mt-6">
                  <div className="rounded-full h-8 w-8 bg-gray-400 mb-2 flex items-center justify-center">
                    <span className="text-white font-bold text-lg">⏹</span>
                  </div>
                  <span className="text-gray-700 font-semibold">Meeting Ended</span>
                </div>
              )}
              {meetingStatus === 'transcription' && (
                <div className="mt-6">
                  <h4 className="font-semibold mb-2 text-green-700">Transcription</h4>
                  <pre className="bg-gray-100 rounded p-3 text-gray-800 whitespace-pre-wrap break-words text-sm max-h-60 overflow-auto">
                    {mockTranscript}
                  </pre>
                  <button
                    className="w-full mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
                    onClick={() => setShowTodoList(v => !v)}
                  >
                    Create To-Do List for Action Items
                  </button>

                  {showTodoList && (
                    <div className="mt-4 bg-neutral-900 rounded-lg p-4 border border-neutral-700">
                      <h3 className="text-md font-bold text-blue-400 mb-2">To-Do List for Action Items</h3>
                      <ul>
                        {(editMode ? editedItems : todoItems).map((item, idx) => (
                          <li key={idx} className="flex items-center mb-3">
                            <input type="checkbox" className="mr-3" checked />
                            {editMode ? (
                              <input
                                className="bg-neutral-800 text-gray-100 rounded px-2 py-1 flex-1"
                                value={editedItems[idx]}
                                onChange={e => {
                                  const newItems = [...editedItems];
                                  newItems[idx] = e.target.value;
                                  setEditedItems(newItems);
                                }}
                              />
                            ) : (
                              <span className="text-gray-100">{item}</span>
                            )}
                          </li>
                        ))}
                      </ul>
                      <div className="flex gap-2 mt-4">
                        <button
                          className="flex-1 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition"
                          onClick={() => {
                            // Save logic here
                            setTodoItems([...todoItems]);
                            setEditMode(false);
                          }}
                        >
                          Save
                        </button>
                        <button
                          className="flex-1 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
                          onClick={() => {
                            setEditedItems([...todoItems]);
                            setEditMode(e => !e);
                          }}
                        >
                          Edit
                        </button>
                        <button
                          className="flex-0 px-4 py-2 bg-gray-700 text-white rounded hover:bg-gray-800 transition text-xl font-bold"
                          onClick={() => {
                            setEditMode(true);
                            setEditedItems([...editMode ? editedItems : todoItems, ""]);
                          }}
                          title="Add Action Point"
                        >
                          +
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              )}
            </>
          )}
        </div>
        {/* History Panel */}
        <HistorySidebar
          open={historyOpen}
          setHistoryOpen={setHistoryOpen}
          meetingHistory={meetingHistory}
        />
        {/* Create Todo Modal */}
        <CreateTodoModal
          open={modalOpen}
          onSave={handleCreateTodo}
          onCancel={() => setModalOpen(false)}
        />
        {/* To-Do List Modal */}
        {showTodoModal && (
          <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50">
            <div className="bg-neutral-900 p-6 rounded-lg w-96 shadow-2xl border border-neutral-700">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-lg font-bold text-blue-400">To-Do List for Action Items</h2>
                <button
                  className="text-gray-400 hover:text-red-400 text-2xl font-bold"
                  onClick={() => setShowTodoModal(false)}
                >&times;</button>
              </div>
              <ul>
                {todoItems.map((item, idx) => (
                  <li key={idx} className="flex items-center mb-3">
                    <input type="checkbox" className="mr-3" />
                    <span className="text-gray-100">{item}</span>
                  </li>
                ))}
              </ul>
              <button
                className="w-full mt-4 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition"
                onClick={() => setShowTodoModal(false)}
              >
                Save To-Do List
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
