import React from 'react';
import { FaHistory, FaListAlt } from 'react-icons/fa';


export default function Sidebar({
  open = true,
  onClose,
  title,
  icon,
  children,
  position = 'right', // 'left' or 'right'
  width = 'w-64',
}) {
  return (
    <div
      className={`
        mt-10 ${position === 'right' ? 'right-0' : 'left-0'}
        h-full ${width} bg-[#181b1b] shadow-2xl z-50 flex flex-col
        transition-transform duration-300
        ${open ? 'translate-x-0' : position === 'right' ? 'translate-x-full' : '-translate-x-full'}
      `}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-700">
        <div className="flex items-center gap-2">
          {icon}
          <span className="font-bold text-lg text-white">{title}</span>
        </div>
        {onClose && (
          <button onClick={onClose} className="text-gray-400 hover:text-red-500 text-2xl font-bold">&times;</button>
        )}
      </div>
      <div className="flex-1 overflow-y-auto p-4">
        {children}
      </div>
    </div>
  );
}
