import React from 'react';
import { FaHistory } from 'react-icons/fa';


export default function HistorySidebar({ open, setHistoryOpen, meetingHistory }) {
  return (
    <>
      {/* Floating History Icon Button (only when sidebar is minimized) */}
      {!open && (
        <button
          className="fixed top-[64px] right-8 bg-[#181b1b] hover:bg-[#232626] p-3 rounded-full shadow z-40"
          title="View Meeting History"
          onClick={() => setHistoryOpen(true)}
        >
          <FaHistory size={28} className="text-cyan-400" />
        </button>
      )}

      {/* Sidebar */}
      <div
        className={`
          fixed right-0 h-full w-72 bg-[#181b1b] shadow-2xl flex flex-col
          transition-transform duration-300
          ${open ? 'translate-x-0' : 'translate-x-full'}
        `}
        style={{
          minWidth: '288px',
          top: '64px', // Match your navbar height
          zIndex: 30,
          height: 'calc(100% - 64px)',
        }}
      >
        {/* Top bar with History icon and Close icon */}
        <div className="flex items-center justify-between px-6 py-5 border-b border-gray-800">
          <div className="flex items-center gap-3">
            <FaHistory className="text-cyan-400 text-2xl" />
            <span className="font-bold text-lg text-white">History</span>
          </div>
          <button
            onClick={() => setHistoryOpen(false)}
            className="text-gray-400 hover:text-red-500 text-2xl font-bold"
            title="Close"
          >
            &times;
          </button>
        </div>
        {/* History content */}
        <div className="flex-1 overflow-y-auto px-6 py-4">
          {meetingHistory.length === 0 && (
            <div className="text-gray-500 text-center mt-10 text-lg">No meeting history yet.</div>
          )}
          {meetingHistory.map((item, idx) => (
            <div key={idx} className="mb-6 border-b border-gray-700 pb-4">
              <div className="font-semibold text-white">{item.title}</div>
              <div className="text-xs text-gray-400 mb-2">{item.date}</div>
              <pre className="bg-[#232626] rounded p-2 text-gray-200 whitespace-pre-wrap break-words text-xs max-h-32 overflow-auto">{item.transcript}</pre>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

