import React, { useState, useRef, useEffect } from 'react';
import { chatService } from '../services/api';

const welcomeInfo = `üìú User Story üìù Test Case Generation ü§ñ Automation Scripts üêû Bugs Creation üéë Image analysis for UI element\n`;

export default function AIChatbotAssistan() {
  // Chat state
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const audioRef = useRef(null);
  const messagesEndRef = useRef(null);
  const [attachedFile, setAttachedFile] = useState(null);

  // Send message handler
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages(prev => [...prev, { sender: 'user', message: input }]);
    setInput('');
    setAttachedFile(null);
    setLoading(true);
    try {
      const res = await chatService.sendMessage(input);
      const aiMsg = res.data.message;
      if (aiMsg && aiMsg.trim()) {
        setMessages(prev => [...prev, { sender: 'ai', message: aiMsg }]);
      }
    } catch {
      setMessages(prev => [...prev, { sender: 'ai', message: 'Error from AI.' }]);
    }
    setLoading(false);
  };

  // File attach handler
  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setAttachedFile(e.target.files[0]);
    }
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    if (messages.length > 0 && messages[messages.length - 1].sender === 'ai') {
      audioRef.current && audioRef.current.play().catch(() => {});
    }
  }, [messages]);

  return (
    <div className="min-h-screen bg-white text-gray-900 dark:bg-neutral-900 dark:text-gray-100 flex flex-col p-8">
      {/* Header */}
      <div className="flex items-center gap-4 mb-6">
        <h2 className="text-2xl font-bold">üëã Welcome to Zap‚ö°Ô∏è - Your AI Assistant!</h2>
      </div>
      {/* Welcome Info */}
      <div className="rounded-xl shadow p-6 mb-6 border border-neutral-200 bg-neutral-100 dark:bg-neutral-800 dark:border-neutral-700">
        <span className="whitespace-pre-line text-lg leading-relaxed">{welcomeInfo}</span>
      </div>
      {/* Chat area */}
      <div className="flex-1 rounded-xl shadow p-6 flex flex-col gap-6 mb-4 overflow-y-auto border border-neutral-200 bg-neutral-50 dark:bg-neutral-900 dark:border-neutral-700">
        {messages.length === 0 ? (
          <div className="text-gray-400 text-base dark:text-gray-300">Ask me anything about test cases, automation, bugs, or UI analysis!</div>
        ) : (
          messages.map((msg, idx) => (
            <div key={idx} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}> 
              <div className={`w-auto max-w-5xl p-4 rounded-lg shadow-sm break-words whitespace-pre-line ${msg.sender === 'user' ? 'bg-blue-900/30 text-blue-200 dark:bg-blue-900/60 dark:text-blue-100' : 'bg-neutral-100 text-gray-900 dark:bg-neutral-800 dark:text-gray-100'}`}> 
                {msg.message.split('\n').map((line, i) => <div key={i}>{line}</div>)}
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
        {loading && <div className="self-start text-xs text-gray-400 dark:text-gray-300">AI is typing...</div>}
      </div>
      {/* Attach file and input area (unified, modern, matches chatbox theme) */}
      <form className="flex items-center gap-2 mt-6 mb-2 bg-neutral-900 dark:bg-neutral-900 rounded-b-2xl rounded-t-2xl px-4 py-4 border border-neutral-800" onSubmit={sendMessage} style={{boxShadow: '0 2px 8px 0 #0002'}}>
        <label className="flex items-center cursor-pointer mr-2">
          <span className="inline-flex items-center justify-center w-10 h-10 rounded-full border-2 border-blue-400 bg-neutral-900 text-blue-400 hover:bg-blue-950 hover:border-blue-500 dark:bg-neutral-900 dark:border-blue-400 dark:text-blue-400 dark:hover:bg-blue-950 transition">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2.2} stroke="currentColor" className="w-6 h-6">
              <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 9V5.25A2.25 2.25 0 0013.5 3h-3A2.25 2.25 0 008.25 5.25V9m7.5 0v9A2.25 2.25 0 0113.5 20.25h-3A2.25 2.25 0 018.25 18V9m7.5 0H8.25" />
            </svg>
          </span>
          <input type="file" className="hidden" onChange={handleFileChange} />
        </label>
        <div className="flex-1 flex items-center">
          <input
            className="w-full rounded-full px-6 py-3 border-2 border-blue-400 focus:border-blue-500 outline-none bg-white text-gray-900 placeholder-gray-400 text-lg transition dark:bg-neutral-900 dark:text-gray-100 dark:placeholder-gray-400 dark:border-blue-400 dark:focus:border-blue-500"
            type="text"
            placeholder="Type your message..."
            value={input}
            onChange={e => setInput(e.target.value)}
            disabled={loading}
            style={{boxShadow: '0 0 0 2px #2563eb22'}} // blue-600/20
          />
        </div>
        <button
          className="ml-2 px-8 py-3 rounded-full bg-[#10a3f9] text-white text-lg font-semibold shadow hover:bg-[#0e8ad6] transition disabled:opacity-60"
          type="submit"
          disabled={loading || !input.trim()}
          style={{minWidth: 90}}
        >Send</button>
      </form>
      {attachedFile && (
        <div className="flex items-center gap-2 mt-1 mb-2 ml-2">
          <span className="text-sm text-gray-300 dark:text-gray-200 truncate max-w-xs">{attachedFile.name}</span>
          <button type="button" className="text-xs text-red-400 hover:text-red-600" onClick={() => setAttachedFile(null)} title="Remove file">‚úï</button>
        </div>
      )}
      {/* Audio for bot message */}
      <audio ref={audioRef} preload="auto">
        <source src="/sounds/send.wav" type="audio/wav" />
        Your browser does not support the audio element.
      </audio>
    </div>
  );
}
