import { http } from 'msw';
// import testCases from '../mock-data/test-cases.json';
// import bugs from '../mock-data/bugs.json';
import dashboardData from '../mock-data/dashboard';

export const handlers = [
  // Mock GET /api/test-cases
  // http.get('/api/test-cases', (req, res, ctx) => {
  //   return res(ctx.status(200), ctx.json(testCases));
  // }),

  // Mock GET /api/bugs
  // http.get('/api/bugs', (req, res, ctx) => {
  //   return res(ctx.status(200), ctx.json(bugs));
  // }),

  // Mock POST /api/login
  http.post('/api/login', () => {
    return new Response(
      JSON.stringify({ token: 'mock-token', user: { name: 'Demo User' } }),
      {
        status: 200,
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  }),

  http.get('/api/applicationsettings', () => {
    return new Response(
      JSON.stringify({
        theme: 'dark',
        notifications: true,
        testCoverage: 94,
        assignedTasks: 6,
        testCasesCreated: 30,
        bugsReported: 12,
        automationScripts: 8,
        failedTests: 2,
      }),
      {
        status: 200,
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  }),

  http.get('/api/activity-logs', () => {
    return new Response(
      JSON.stringify([
        { id: 'AL-001', description: 'Manual test run completed', date: '2024-06-01', status: 'Passed' },
        { id: 'AL-002', description: 'Automated test failed', date: '2024-06-02', status: 'Failed' },
      ]),
      {
        status: 200,
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  }),

  http.get('/api/settings/profile', () => {
    return new Response(
      JSON.stringify({
        name: 'Demo User',
        email: 'demo@zap.com',
        role: 'QA',
        avatar: 'https://ui-avatars.com/api/?name=Demo+User'
      }),
      {
        status: 200,
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  }),

  http.put('/api/settings/profile', () => {
    return new Response(null, { status: 200 });
  }),

  http.get('/api/dashboard', () => {
    return new Response(
      JSON.stringify(dashboardData),
      {
        status: 200,
        headers: {
          'Content-Type': 'application/json',
        },
      }
    );
  }),

  // Add more handlers as needed...
];
