import React, { useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import ProductList from './ProductList';

export default function TopNavBar() {
  const [productsOpen, setProductsOpen] = useState(false);
  const productsBtnRef = useRef();

  // Close dropdown on mouse leave (with a small delay for UX)
  let closeTimeout = null;
  const handleMouseEnter = () => {
    clearTimeout(closeTimeout);
    setProductsOpen(true);
  };
  const handleMouseLeave = () => {
    closeTimeout = setTimeout(() => setProductsOpen(false), 150);
  };

  return (
    <nav className="fixed top-0 left-0 right-0 bg-[#1A1E1D] text-white flex items-center justify-between px-6 h-14 shadow z-50">
      <div className="flex items-center gap-4">
        <h1 className="text-2xl font-bold tracking-wide">Zap⚡️</h1>
        <div className="hidden md:flex gap-8 ml-8 items-center">
          <Link to="/zap-ai-features" className="hover:text-blue-300">Zap AI Features</Link>
          <Link to="/my-sprint" className="hover:text-blue-300">My Sprint</Link>
          <Link to="/live-test" className="hover:text-blue-300">Live Test</Link>
          <Link to="/AutomationTest" className="hover:text-blue-300">Automated Test</Link>
          <div
            className="relative"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            <button
              ref={productsBtnRef}
              className={`flex items-center gap-1 px-2 py-1 rounded hover:text-blue-300 focus:outline-none ${productsOpen ? 'text-blue-400' : ''}`}
              onClick={() => setProductsOpen((open) => !open)}
              aria-haspopup="true"
              aria-expanded={productsOpen}
            >
              Zap⚡️ Products
              <svg className={`w-4 h-4 ml-1 transition-transform ${productsOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" /></svg>
            </button>
            <ProductList open={productsOpen} onClose={() => setProductsOpen(false)} />
          </div>
        </div>
      </div>
      <div className="flex items-center gap-4">
        <button>🔔</button>
        <a href="#" className="hover:text-blue-300">Invite team</a>
        <a href="#" className="hover:text-blue-300">Plans & Pricing</a>
        <button className="hover:text-blue-300" title="Help"><span role="img" aria-label="help">❓</span></button>
        <button className="hover:text-blue-300" title="Account"><span role="img" aria-label="user">👤</span></button>
        <button className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600">Buy a plan</button>
      </div>
    </nav>
  );
}
