import React, { useState } from 'react';
import { Link } from 'react-router-dom';

export default function TopNavBar() {
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([
    { id: 1, text: 'New test run completed successfully.' },
    { id: 2, text: 'Bug created: Login page error.' },
    { id: 3, text: 'Sprint review meeting at 3 PM.' },
  ]);

  const handleClearNotifications = () => setNotifications([]);

  return (
    <nav className="fixed top-0 left-0 right-0 bg-[#1A1E1D] text-white flex items-center justify-between px-6 h-14 shadow z-50">
      <div className="flex items-center gap-4">
        <h1 className="text-2xl font-bold tracking-wide">Zap⚡️</h1>
        <div className="hidden md:flex gap-8 ml-8 items-center">
          <Link to="/zap-ai-features" className="hover:text-blue-300">Zap AI Features</Link>
          <Link to="/my-sprint" className="hover:text-blue-300">My Sprint</Link>
          <Link to="/live-test" className="hover:text-blue-300">Live Test</Link>
          <Link to="/AutomationTest" className="hover:text-blue-300">Automated Test</Link>
        </div>
      </div>
      <div className="flex items-center gap-4 relative">
        <div className="relative">
          <button onClick={() => setShowNotifications((v) => !v)} className="relative focus:outline-none">
            <span role="img" aria-label="notifications">🔔</span>
            {notifications.length > 0 && (
              <span className="absolute -top-1 -right-1 bg-red-500 text-white rounded-full text-xs px-1">{notifications.length}</span>
            )}
          </button>
          {showNotifications && (
            <div className="absolute right-0 mt-2 w-80 bg-white text-black rounded shadow-lg z-50 border border-gray-200" style={{ minWidth: '18rem' }}>
              <div className="p-3 border-b font-bold flex items-center justify-between">
                <span>Notifications</span>
                <button
                  className="text-xs text-blue-600 hover:underline px-2 py-1 rounded"
                  onClick={handleClearNotifications}
                  disabled={notifications.length === 0}
                >
                  Clear All
                </button>
              </div>
              <ul className="max-h-60 overflow-y-auto">
                {notifications.length === 0 ? (
                  <li className="px-4 py-6 text-center text-gray-400 text-sm">No notifications</li>
                ) : (
                  notifications.map(n => (
                    <li key={n.id} className="px-4 py-2 border-b last:border-b-0 hover:bg-gray-100 text-sm transition-colors">
                      {n.text}
                    </li>
                  ))
                )}
              </ul>
              <button className="w-full text-center py-2 text-blue-600 hover:underline bg-gray-50 rounded-b" onClick={() => setShowNotifications(false)}>
                Close
              </button>
            </div>
          )}
        </div>
        <a href="#" className="hover:text-blue-300">Invite team</a>
        <a href="#" className="hover:text-blue-300">Plans & Pricing</a>
        <button className="hover:text-blue-300" title="Help"><span role="img" aria-label="help">❓</span></button>
        <button className="hover:text-blue-300" title="Account"><span role="img" aria-label="user">👤</span></button>
        <button className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600">Buy a plan</button>
      </div>
    </nav>
  );
}
