import { http } from 'msw';
// import testCases from '../mock-data/test-cases.json';
// import bugs from '../mock-data/bugs.json';

export const handlers = [
  // Mock GET /api/test-cases
  // http.get('/api/test-cases', (req, res, ctx) => {
  //   return res(ctx.status(200), ctx.json(testCases));
  // }),

  // Mock GET /api/bugs
  // http.get('/api/bugs', (req, res, ctx) => {
  //   return res(ctx.status(200), ctx.json(bugs));
  // }),

  // Mock POST /api/login
  http.post('/api/login', (req, res, ctx) => {
    return res(
      ctx.status(200),
      ctx.json({ token: 'mock-token', user: { name: 'Demo User' } })
    );
  }),

  http.get('/api/applicationsettings', (req, res, ctx) => {
    return res(
      ctx.status(200),
      ctx.json({
        theme: 'dark',
        notifications: true,
        testCoverage: 94,
        assignedTasks: 6,
        testCasesCreated: 30,
        bugsReported: 12,
        automationScripts: 8,
        failedTests: 2,
        // Add more as needed for your dashboard
      })
    );
  }),

  http.get('/api/activity-logs', (req, res, ctx) => {
    return res(
      ctx.status(200),
      ctx.json([
        { id: 'AL-001', description: 'Manual test run completed', date: '2024-06-01', status: 'Passed' },
        { id: 'AL-002', description: 'Automated test failed', date: '2024-06-02', status: 'Failed' },
        // Add more as needed
      ])
    );
  }),

  http.get('/api/settings/profile', (req, res, ctx) => {
    return res(
      ctx.status(200),
      ctx.json({
        name: 'Demo User',
        email: 'demo@zap.com',
        role: 'QA',
        avatar: 'https://ui-avatars.com/api/?name=Demo+User'
      })
    );
  }),

  http.put('/api/settings/profile', (req, res, ctx) => {
    return res(ctx.status(200));
  }),

  http.get('/api/dashboard', (req, res, ctx) => {
    return res(
      ctx.status(200),
      ctx.json({ message: 'Hello from mock dashboard!' })
    );
  }),

  // Add more handlers as needed...
];
