import React, { useState } from 'react';

export default function CreateTodoModal({ open, onSave, onCancel }) {
  const [todos, setTodos] = useState(['']);

  const handleTodoChange = (idx, value) => {
    const updated = [...todos];
    updated[idx] = value;
    setTodos(updated);
  };

  const addTodo = () => setTodos([...todos, '']);

  return open ? (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-[#232626] p-6 rounded-lg w-96">
        <h2 className="text-lg font-bold text-white mb-4">Create To-Do List</h2>
        {todos.map((todo, idx) => (
          <input
            key={idx}
            className="w-full mb-2 p-2 rounded bg-[#181b1b] text-white"
            value={todo}
            onChange={e => handleTodoChange(idx, e.target.value)}
            placeholder={`Task ${idx + 1}`}
          />
        ))}
        <button
          className="text-cyan-400 mb-4"
          onClick={addTodo}
        >+ Add another task</button>
        <div className="flex justify-end gap-4 mt-4">
          <button
            className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700"
            onClick={onCancel}
          >Cancel</button>
          <button
            className="px-4 py-2 bg-cyan-500 text-white rounded hover:bg-cyan-600"
            onClick={() => onSave(todos.filter(t => t.trim() !== ''))}
          >Save</button>
        </div>
      </div>
    </div>
  ) : null;
}
