import React, { useState } from 'react';
import { FaHistory } from 'react-icons/fa';

// Mock meeting history data
const mockMeetingHistory = [
	{
		title: 'Sprint Planning',
		date: '2025-06-16',
		notes: 'Discussed sprint goals and assigned tasks.',
		todo: [
			'Finalize user stories',
			'Assign tasks to team members',
			'Update Jira board',
		],
		transcript:
			"Alice: Let's start with the sprint goals. Bob: We need to finish the dashboard. Carol: I'll take the API integration.",
	},
	{
		title: 'Refinement Meeting',
		date: '2025-06-18',
		notes: 'Reviewed backlog and clarified requirements.',
		todo: [
			'Clarify ticket #123',
			'Estimate new stories',
			'Prepare questions for product owner',
		],
		transcript:
			"Dave: Can we clarify ticket #123? Eve: It needs more acceptance criteria. Frank: I'll update the story points.",
	},
];

export default function HistorySidebar({ open, setHistoryOpen, meetingHistory }) {
	const [history, setHistory] = useState([]);
	const historyData = meetingHistory && meetingHistory.length > 0 ? meetingHistory : mockMeetingHistory;
	const [selected, setSelected] = useState(null);
	const [showTodoList, setShowTodoList] = useState(false);
	const [showSuccess, setShowSuccess] = useState(false);

	// Save handler
	const handleSave = () => {
		setShowTodoList(false); // Hide the To-Do panel
		setShowSuccess(true);   // Show the message
		setTimeout(() => setShowSuccess(false), 10000); // Hide after 10s
		// ...add your logic to actually save to history/assigned tasks...
	};

	return (
		<>
			{/* Floating History Icon Button (only when sidebar is minimized) */}
			{!open && (
				<button
					className="mt-10 fixed top-20 right-2 fixed top-[64px] right-8 bg-[#181b1b] hover:bg-[#232626] p-3 rounded-full shadow z-40"
					title="View Meeting History"
					onClick={() => setHistoryOpen(true)}
				>
					<FaHistory size={28} className="text-cyan-400" />
				</button>
			)}

			{/* Sidebar */}
			<div
				className={`
          mt-8 fixed top-20 right-2 fixed right-0 h-full w-72 bg-[#181b1b] shadow-2xl flex flex-col
          transition-transform duration-300
          ${open ? 'translate-x-0' : 'translate-x-full'}
        `}
				style={{
					minWidth: '288px',
					top: '64px', // Match your navbar height
					zIndex: 30,
					height: 'calc(100% - 64px)',
				}}
			>
				{/* Top bar with History icon and Close icon */}
				<div className="flex items-center justify-between px-6 py-5 border-b border-gray-800">
					<div className="flex items-center gap-3">
						<FaHistory className="text-cyan-400 text-2xl" />
						<span className="font-bold text-lg text-white">History</span>
					</div>
					<button
						onClick={() => setHistoryOpen(false)}
						className="text-gray-400 hover:text-red-500 text-2xl font-bold"
						title="Close"
					>
						&times;
					</button>
				</div>
				{/* History cards */}
				<div className="flex-1 overflow-y-auto px-4 py-4">
					{(!historyData || historyData.length === 0) && (
						<div className="text-gray-500 text-center mt-10 text-lg">
							No meeting history yet.
						</div>
					)}
					{(historyData || []).map((item, idx) => (
						<div
							key={idx}
							className="bg-[#232626] rounded-lg p-4 mb-4 cursor-pointer hover:bg-[#2d3131] border border-gray-700"
							onClick={() => setSelected(item)}
						>
							<div className="font-semibold text-white text-base truncate">
								{item.title}
							</div>
							<div className="text-xs text-gray-400 truncate">
								{item.notes}
							</div>
						</div>
					))}
				</div>
			</div>

			{/* Popup modal for meeting details */}
			{selected && (
				<div
					className="fixed top-[64px] h-[calc(100%-64px)] w-[400px] bg-[#232626] shadow-2xl z-50 p-6 flex flex-col"
					style={{
						borderTopLeftRadius: '1rem',
						borderBottomLeftRadius: '1rem',
						right: '288px', // width of your sidebar (w-72 = 288px)
					}}
				>
					<div className="flex items-center justify-between mb-4">
						<div className="font-bold text-lg text-white">
							{selected.title}
						</div>
						<button
							onClick={() => setSelected(null)}
							className="text-gray-400 hover:text-red-500 text-2xl font-bold"
							title="Close"
						>
							&times;
						</button>
					</div>
					<div className="text-xs text-gray-400 mb-2">
						{selected.date}
					</div>
					<div className="mb-2">
						<span className="text-cyan-300 font-semibold">Notes:</span>
						<div className="text-gray-200 text-sm">
							{selected.notes}
						</div>
					</div>
					<div className="mb-2">
						<span className="text-cyan-300 font-semibold">Todo List:</span>
						<ul className="list-disc list-inside text-gray-200 text-sm">
							{(selected.todo || []).map((todo, i) => (
								<li key={i}>{todo}</li>
							))}
						</ul>
					</div>
					<div>
						<span className="text-cyan-300 font-semibold">
							Transcription:
						</span>
						<pre className="bg-[#181b1b] rounded p-2 text-gray-200 whitespace-pre-wrap break-words text-xs max-h-32 overflow-auto">
							{selected.transcript}
						</pre>
					</div>
				</div>
			)}
		</>
	);
}

// Inside your component:
