import React from 'react';
import { FaHistory, FaArrowLeft } from 'react-icons/fa';


export default function HistorySidebar({ open, onClose, onBack, meetingHistory }) {
  return (
    <div
      className={`
        fixed top-0 right-0 h-full w-64 bg-[#181b1b] shadow-2xl z-50 flex flex-col
        transition-transform duration-300
        ${open ? 'translate-x-0' : 'translate-x-full'}
      `}
    >
      {/* Top bar with icons */}
      <div className="flex items-center justify-between p-4 border-b border-gray-700 relative">
        {/* Back icon (optional) */}
        {onBack && (
          <button
            onClick={onBack}
            className="absolute left-2 top-1/2 -translate-y-1/2 text-gray-400 hover:text-cyan-400 text-xl"
            title="Back"
          >
            <FaArrowLeft />
          </button>
        )}
        {/* History icon and title */}
        <div className="flex items-center gap-2 mx-auto">
          <FaHistory className="text-cyan-400 text-2xl" />
          <span className="font-bold text-lg text-white">History</span>
        </div>
        {/* Close icon */}
        <button
          onClick={onClose}
          className="absolute right-2 top-1/2 -translate-y-1/2 text-gray-400 hover:text-red-500 text-2xl font-bold"
          title="Close"
        >
          &times;
        </button>
      </div>
      {/* History content */}
      <div className="flex-1 overflow-y-auto p-4">
        {meetingHistory.length === 0 && (
          <div className="text-gray-500 text-center mt-10">No meeting history yet.</div>
        )}
        {meetingHistory.map((item, idx) => (
          <div key={idx} className="mb-6 border-b border-gray-700 pb-4">
            <div className="font-semibold text-white">{item.title}</div>
            <div className="text-xs text-gray-400 mb-2">{item.date}</div>
            <pre className="bg-[#232626] rounded p-2 text-gray-200 whitespace-pre-wrap break-words text-xs max-h-32 overflow-auto">{item.transcript}</pre>
          </div>
        ))}
      </div>
    </div>
  );
}
