import React, { useState } from 'react';

export default function VoiceAssistantModal({ onClose }) {
  const [status, setStatus] = useState('idle');
  const [transcript, setTranscript] = useState('');
  const [requirements, setRequirements] = useState([]);
  const [testCases, setTestCases] = useState([]);

  // Use mock data for demonstration
  const handleStart = () => {
    setStatus('processing');
    setTimeout(() => {
      setTranscript(
        "As a user, I want to login so that I can access my dashboard. The system should validate credentials and show an error for invalid login."
      );
      setRequirements([
        "User login functionality",
        "Credential validation",
        "Error handling for invalid login"
      ]);
      setTestCases([
        "Test login with valid credentials",
        "Test login with invalid credentials",
        "Test error message on failed login"
      ]);
      setStatus('done');
    }, 1500); // Simulate processing delay
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={onClose}>
      <div className="bg-white rounded-lg shadow-lg p-6 relative max-w-lg w-full" onClick={e => e.stopPropagation()}>
        <button
          onClick={onClose}
          className="absolute top-2 right-2 text-gray-500 hover:text-gray-800 text-2xl font-bold"
          aria-label="Close"
        >
          &times;
        </button>
        <h2 className="text-xl text-black-500 font-bold mb-4">AI Voice Assistant</h2>
        <p className="mb-4">Join a meeting, transcribe, extract requirements, and generate test cases automatically.</p>
        <button
          className="bg-blue-600 text-white px-4 py-2 rounded mb-4"
          onClick={handleStart}
          disabled={status === 'processing' || status === 'done'}
        >
          {status === 'idle' ? 'Start Meeting Assistant' : 'Processing...'}
        </button>
        {transcript && (
          <>
            <h3 className="font-semibold mt-4">Transcript</h3>
            <pre className="bg-black-100 p-2 rounded">{transcript}</pre>
          </>
        )}
        {requirements.length > 0 && (
          <>
            <h3 className="font-semibold mt-4">Extracted Requirements</h3>
            <ul className="list-disc ml-6">{requirements.map((r, i) => <li key={i}>{r}</li>)}</ul>
          </>
        )}
        {testCases.length > 0 && (
          <>
            <h3 className="font-semibold mt-4">Generated Test Cases</h3>
            <ul className="list-disc ml-6">{testCases.map((t, i) => <li key={i}>{t}</li>)}</ul>
          </>
        )}
      </div>
    </div>
  );
}
